import java.util.ArrayList;
import java.util.Collections;
import java.util.Scanner;
public class Main {
    static ArrayList Num;
    static int N;
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        N = Integer.parseInt(sc.next());
        ArrayList> permutation=exponent(1);
        System.out.println("");
        for(ArrayList c : permutation){
            Collections.reverse(c);
            for(int i :c){
                System.out.print(i+" ");
            }
            System.out.print("\n");
        }
    }

    /**
     * 第n个数之后的数
     * @param i
     */
    public static ArrayList> exponent(int i){
        if(i == N) {
            ArrayList> a = new ArrayList>();
            ArrayList b=new ArrayList();
            b.add((N));
            a.add(b);
            return a;
        }
        ArrayList> a=exponent(i+1);
        ArrayList> b=new ArrayList<>();
        b.addAll(a);
        ArrayList d=new ArrayList();
        d.add(i);
        b.add(d);
        for(ArrayList c : a){
            ArrayList e = new ArrayList<>();
            e.addAll(c);
            e.add(i);
            b.add(e);
        }
        return b;
    }
}

