    public static void main(String args[]) throws IOException{
        in = new BufferedReader(new InputStreamReader(System.in));
        Scanner sc = new Scanner(System.in);
        M=sc.nextInt();
        N=sc.nextInt();
        P=sc.nextInt();
//        System.out.println(M+N+P);
        names = new String[M];
        testimoneies = new String[P];
        speakers = new String[P];
        sentences = new String[P];
        map = new HashMap<>();
        String name;
        String tempString="";
        //名字数组
        tempString=sc.nextLine();
        for(int i =0;i entry : map.entrySet()) {
            //获取假设的罪犯的名字
            criminal=entry.getKey();
//            System.out.println(criminal);
            //循环假设一周七天哪一天是今天
            for(int j=0;j copyMap = new HashMap<>();    
                copyMap.putAll(map);
//                System.out.println(copyMap);
                //2.循环判断P句证词
                day:
                for(int k=0;k> it =copyMap.entrySet().iterator();    //遍历名字
                while(it.hasNext()) {
                    Map.Entry en =it.next();
                    if(en.getValue() == 1) count++;
                    else if(en.getValue() == -1) count1++;
                }
//                System.out.println(copyMap);
                if(count=N&&(tempString == ""||!tempString.equals(criminal))) {num++;tempString=criminal;}
                if(num>1) break;
                }
            }
