    static ArrayList Num;
    static int N;
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        N = Integer.parseInt(sc.next());
        ArrayList> permutation=exponent(1);
        System.out.println("");
        for(ArrayList c : permutation){
            Collections.reverse(c);
            for(int i :c){
