            }
            System.out.print("\n");
        }
    }

    /**
     * 第n个数之后的数
     * @param i
     */
    public static ArrayList> exponent(int i){
        if(i == N) {
            ArrayList> a = new ArrayList>();
            ArrayList b=new ArrayList();
            b.add((N));
            a.add(b);
            return a;
        }
        ArrayList> a=exponent(i+1);
        ArrayList> b=new ArrayList<>();
        b.addAll(a);
        ArrayList d=new ArrayList();
        d.add(i);
        b.add(d);
        for(ArrayList c : a){
            ArrayList e = new ArrayList<>();
            e.addAll(c);
            e.add(i);
            b.add(e);
        }
        return b;
    }
}

